import { validationMessages } from '@state/schemas/sections/validationMessages';

// Port Interface (ADR-12)
export interface AuditEvent {
  timestamp: string;
  level: 'info' | 'warning' | 'error' | 'fatal';
  message: string;
  context?: Record<string, unknown>;
}

export interface AuditLoggerPort {
  logEvent(event: AuditEvent): void;
  getEventsByLevel(level: AuditEvent['level']): AuditEvent[];
}

class AuditLoggerAdapter implements AuditLoggerPort {
  private eventBuffer: AuditEvent[] = [];

  logEvent(event: AuditEvent): void {
    // 1. VERTALING: Kijk of we een menselijke tekst hebben voor deze code
    const translatedMessage = this.translate(event.message);
    if (translatedMessage) {
      event.context = { ...event.context, originalCode: event.message };
      event.message = translatedMessage;
    }

    // 2. ROUTERING: De Luchtverkeerstoren logica
    if (event.message === 'SYSTEM_ERROR' || event.message === 'VALIDATION_CRASH') {
      this.routeToTicketing(event);
      event.level = 'fatal';
    }

    if (event.level === 'error' || event.level === 'warning') {
      this.routeToUI(event);
    }

    this.routeToConsole(event);
    this.eventBuffer.push(event);
  }

  private routeToConsole(event: AuditEvent): void {
    const prefix = `[AUDIT]`;
    const payload = JSON.stringify({
      level: event.level,
      message: event.message,
      timestamp: event.timestamp,
      context: event.context
    });
    console.log(prefix, payload);
  }

  private routeToUI(_event: AuditEvent): void {
    // Voor de UI componenten (underscore voorkomt linter error)
  }

  private routeToTicketing(event: AuditEvent): void {
    console.error('!!! TICKETING/MAIL ALERT !!!', event);
  }

  // Hulpmiddel om door het geneste validationMessages object te graven
  private translate(path: string): string | null {
    try {
      if (!path || typeof path !== 'string') return null;
      return path.split('.').reduce((obj, key) => obj?.[key], validationMessages as any) || null;
    } catch {
      return null;
    }
  }

  getEventsByLevel(level: AuditEvent['level']): AuditEvent[] {
    return this.eventBuffer.filter(e => e.level === level);
  }

  clearBuffer(): void {
    this.eventBuffer = [];
  }
}

export const auditLogger = new AuditLoggerAdapter();

// --- DE HERSTELDE BACKWARD COMPATIBILITY BRIDGE ---
export const Logger = {
  error: (msg: any, err?: any) => {
    const errorObj = err instanceof Error ? err : new Error(String(msg));
    auditLogger.logEvent({
      timestamp: new Date().toISOString(),
      level: 'error',
      message: errorObj.message,
      context: { msg, stack: errorObj.stack }
    });
  },
  warn: (msg: string, data?: any) => {
    auditLogger.logEvent({
      timestamp: new Date().toISOString(),
      level: 'warning',
      message: msg,
      context: data
    });
  },
  info: (msg: string, data?: any) => {
    auditLogger.logEvent({
      timestamp: new Date().toISOString(),
      level: 'info',
      message: msg,
      context: data
    });
  },
  log: (...args: any[]) => {
    const message = String(args[1] || args[0]);
    auditLogger.logEvent({
      timestamp: new Date().toISOString(),
      level: 'info',
      message: message,
      context: { rawArgs: args }
    });
  }
};

export const logger = Logger;
export const AuditLogger = Logger;

import { parseRawCsv } from '@utils/csvHelper';
import { CSVProcessor, CSVKeys } from '@domain/services/CSVProcessor';

export const CSVAdapter = {
  /**
   * De Adapter is alleen verantwoordelijk voor het 'vinden' van de data
   */
  mapToInternalModel: (rawCsv: string) => {
    const processor = new CSVProcessor();
    const rawRows = parseRawCsv(rawCsv) ?? [];

    return rawRows.map((row) => {
      // 1. Zoek de juiste kolommen (Heuristiek blijft in de adapter)
      const keys = Object.keys(row ?? {});

      const detectedKeys: CSVKeys = {
        amount: keys.find((k) => /bedrag|amount|transactie/i.test(k)) || '',
        mutation: keys.find((k) => /Af.?Bij|Mutatie|tegenrekening/i.test(k)),
        description: keys.find((k) => /Naam|Omschrijving|Mededeling|Beschrijving/i.test(k)) || '',
        date: keys.find((k) => /Datum|Boekdatum|date/i.test(k)) || ''
      };

      // 2. Delegeer alle berekeningen en transformaties naar de Processor
      return processor.processRow(row, detectedKeys);
    });
  },

  // Alias voor compatibiliteit met bestaande code
  processCsvData: (rawCsv: string) => {
    return CSVAdapter.mapToInternalModel(rawCsv);
  },
};

import { DATA_KEYS } from '@domain/constants/datakeys';
import { toCents } from '@domain/helpers/numbers';
import { FormState } from '@shared-types/form';
import { Member } from '@domain/household';

/**
 * Migreert een oude state structuur naar de nieuwe Phoenix 2025 interface.
 */
export const migrateToPhoenix = (oldState: any): FormState => {
  const o = oldState || {};

  const getFirstAmountCents = (obj: any): number => {
    const list = obj?.items || obj?.list || [];
    return list.length > 0 ? toCents(list[0]?.amount ?? list[0]?.value ?? 0) : 0;
  };

  const migratedMembers: Member[] = (o[DATA_KEYS.HOUSEHOLD]?.leden || []).map(
    (lid: any, i: number) => ({
      entityId: lid.id || `m-${i}`,
      naam: lid.naam || lid.firstName || 'Lid',
      memberType: lid.type || 'adult',
    }),
  );

  const migratedData: FormState['data'] = {
    [DATA_KEYS.SETUP]: o[DATA_KEYS.SETUP] || {
      aantalMensen: 1,
      aantalVolwassen: 1,
      autoCount: 'Nee',
    },
    [DATA_KEYS.HOUSEHOLD]: {
      members: migratedMembers as any[],
    },
    [DATA_KEYS.FINANCE]: {
      income: {
        items: [],
        totalAmount: getFirstAmountCents(o.C7 || o.income),
      },
      expenses: {
        items: [],
        totalAmount: getFirstAmountCents(o.C10 || o.fixedExpenses),
      },
    },
  };

  return {
    schemaVersion: '1.0',
    activeStep: 'LANDING',
    currentPageId: 'page_1',
    isValid: true,
    data: migratedData,
    meta: {
      lastModified: new Date().toISOString(),
      version: 1,
    },
  };
};

/**
 * Storage service voor persistentie en migratie.
 * Fix: Named export toegevoegd om TS2305 te verhelpen.
 */
export const storage = {
  migrateToPhoenix,
  loadState: async (): Promise<FormState | null> => {
    // Hier kun je de feitelijke AsyncStorage of localStorage logica toevoegen
    return null;
  },
};

import { AuditLogger } from '@/adapters/audit/AuditLoggerAdapter'; // Check of dit pad klopt met je find-resultaat

const allocateRemainder = (total: number, parts: number): number[] => {
  const base = Math.floor(total / parts);
  const remainder = total % parts;
  return Array.from({ length: parts }, (_, i) => (i < remainder ? base + 1 : base));
};

type TransactionState = Record<string, any>;

export class StatefulTransactionAdapter {
  private history: TransactionState[] = [];
  private pointer: number = -1;
  private readonly VERSION = '2025-01-A';

  constructor(initialState: TransactionState) {
    this.push(initialState, 'INIT');
  }

  public push(newState: TransactionState, actionType: string): void {
    if (this.pointer < this.history.length - 1) {
      this.history = this.history.slice(0, this.pointer + 1);
    }

    this.history.push(newState);
    this.pointer++;

    // Fix: level + msg toegevoegd voor TS2555
    AuditLogger.log('INFO', 'transaction_push', {
      type: actionType,
      pointer: this.pointer,
      adr: 'ADR-12',
    });
  }

  public undo(): TransactionState | null {
    if (this.pointer > 0) {
      this.pointer--;
      AuditLogger.log('ACTION', 'undo', { pointer: this.pointer });
      return this.history[this.pointer];
    }
    return null;
  }

  public redo(): TransactionState | null {
    if (this.pointer < this.history.length - 1) {
      this.pointer++;
      AuditLogger.log('ACTION', 'redo', { pointer: this.pointer });
      return this.history[this.pointer];
    }
    return null;
  }

  public calculateDistribution(totalAmount: number, parts: number): number[] {
    return allocateRemainder(Math.floor(totalAmount), parts);
  }

  public getCurrentState(): TransactionState {
    return this.history[this.pointer];
  }
}

