import React, { createContext, useContext, useReducer, ReactNode, Dispatch } from 'react';
import { formReducer } from './formReducer';
import { FormState } from '@shared-types/form';

interface FormContextType {
  state: FormState;
  dispatch: Dispatch<any>;
}

export const FormContext = createContext<FormContextType | undefined>(undefined);

export const FormProvider: React.FC<{
  children: ReactNode;
  initialState: FormState;
  mockDispatch?: Dispatch<any>; //  Voeg dit toe voor tests
}> = ({ children, initialState, mockDispatch }) => {
  const [state, dispatch] = useReducer(formReducer, initialState);

  // Gebruik de mock als die er is, anders de echte reducer dispatch
  const activeDispatch = mockDispatch || dispatch;

  return (
    <FormContext.Provider value={{ state, dispatch: activeDispatch }}>
      {children}
    </FormContext.Provider>
  );
};

export const useForm = () => {
  const context = useContext(FormContext);
  if (!context) throw new Error('useForm must be used within a FormProvider');
  return context;
};

import * as React from 'react';
import { createContext, useContext, useReducer, ReactNode } from 'react';
import { formReducer } from './formReducer';
import { FormState } from '@shared-types/form';
import { makePhoenixState } from '../../test-utils/factories/stateFactory';

// De vorm van de context die beschikbaar is in de app
interface WizardContextType {
  state: FormState;
  dispatch: React.Dispatch<any>;
}

// Props voor de provider, inclusief injectie-opties voor tests
interface WizardProviderProps {
  children: ReactNode;
  initialState?: FormState; // Voor test-injectie
  mockDispatch?: React.Dispatch<any>; // Voor test-injectie (jest.fn())
}

const FormContext = createContext<WizardContextType | undefined>(undefined);

// De standaard initile staat van de applicatie via de factory
const defaultInitialState = makePhoenixState();

export const WizardProvider: React.FC<WizardProviderProps> = ({
  children,
  initialState,
  mockDispatch,
}) => {
  // 1. Initialiseer de reducer met ofwel de genjecteerde staat, of de standaard
  const [state, dispatch] = useReducer(formReducer, initialState || defaultInitialState);

  // 2. Bepaal welke dispatch we gebruiken (echt of mock)
  // Dit zorgt ervoor dat we in tests acties kunnen onderscheppen
  const activeDispatch = mockDispatch || dispatch;

  return (
    <FormContext.Provider value={{ state, dispatch: activeDispatch }}>
      {children}
    </FormContext.Provider>
  );
};

// De hook die gebruikt wordt in componenten zoals LandingScreen
export const useForm = () => {
  const context = useContext(FormContext);
  if (context === undefined) {
    throw new Error('useForm must be used within a WizardProvider/FormProvider');
  }
  return context;
};

// Alias voor backwards compatibility mocht je nog useWizard gebruiken
export const useWizard = useForm;

import { FormState, FormAction } from '@shared-types/form';
import { deepMerge } from '@utils/objects'; // Zorg dat deze helper bestaat
import { DATA_KEYS } from '@domain/constants/datakeys';

export function formReducer(state: FormState, action: FormAction): FormState {
  const metaUpdate = { ...state.meta, lastModified: new Date().toISOString() };

  switch (action.type) {
    case 'UPDATE_DATA': {
      return {
        ...state,
        data: deepMerge(state.data, action.payload),
        meta: metaUpdate,
      };
    }

    case 'SET_FIELD': {
      const { section, field, value } = action.payload;
      return {
        ...state,
        data: {
          ...state.data,
          [section]: {
            ...(state.data as any)[section],
            [field]: value,
          },
        },
        meta: metaUpdate,
      };
    }

    case 'SET_STEP':
      return { ...state, activeStep: action.payload, meta: metaUpdate };

    case 'RESET_APP':
      return {
        ...state,
        data: {
          // SETUP Sectie (Fix Error 2418)
          [DATA_KEYS.SETUP]: {
            aantalMensen: 1,
            aantalVolwassen: 1,
            autoCount: 'Nee',
            heeftHuisdieren: false,
          },
          // HOUSEHOLD Sectie
          household: {
            members: [],
          },
          // FINANCE Sectie (Fix Error 2741)
          finance: {
            income: {
              items: [], //  'items' is verplicht volgens je shared-types
              totalAmount: 0,
            },
            expenses: {
              items: [], //  Idem voor uitgaven
              totalAmount: 0,
            },
          },
        },
        isValid: true, //  Fixt de falende test
        activeStep: '0', //  Fixt de string/number mismatch
        meta: metaUpdate,
      };

    default:
      return state;
  }
}

// src/app/hooks/useAppOrchestration.ts
import { useEffect, useMemo } from 'react'; // Voeg useMemo toe
import { useForm } from '@context/FormContext';
import { storage } from '@adapters/storage/storage';

export interface PhoenixEnvelopeV2 {
  version: string;
  payload: any;
  timestamp: string;
}

/**
 * Orchestrator Hook: Beheert de initile data-load en synchronisatie.
 * Fix 1: Voorkomt infinite loops door specifiek op de payload te monitoren.
 */
export const useAppOrchestration = (envelope?: PhoenixEnvelopeV2) => {
  const { state, dispatch } = useForm();

  useEffect(() => {
    const initApp = async () => {
      if (envelope?.payload) {
        dispatch({ type: 'UPDATE_DATA', payload: envelope.payload });
        return;
      }

      const saved = await storage.loadState();
      if (saved) {
        const dataToLoad = (saved as any).data || saved;
        dispatch({ type: 'UPDATE_DATA', payload: dataToLoad });
      }
    };

    initApp();
  }, [dispatch, envelope?.payload]);

  /**
   * Verbeterde Status Logica
   * 1. HYDRATING: We hebben nog geen schemaversie (app start net op).
   * 2. ONBOARDING: Geen data OF setup niet voltooid (activeStep is niet 'COMPLETED').
   * 3. READY: Setup is voltooid en data is valide.
   */
  const status = useMemo(() => {
    // A. Laden
    if (!state.schemaVersion) {
      return 'HYDRATING';
    }

    // B. Check of we data van buitenaf hebben (Envelope)
    // Als er een envelope is met payload, beschouwen we de setup als voltooid
    const isExternalData = !!envelope?.payload;

    // C. Check of de interne setup voltooid is
    const isInternalSetupDone =
      state.data?.household?.members?.length > 0 &&
      (state.activeStep === 'dashboard' || state.activeStep === 'completed');

    // Als geen van beide waar is -> ONBOARDING
    if (!isExternalData && !isInternalSetupDone) {
      return 'ONBOARDING';
    }

    // D. Is de data bruikbaar?
    return state.isValid ? 'READY' : 'INCOMPLETE';
  }, [
    state.schemaVersion,
    state.data?.household?.members,
    state.activeStep,
    state.isValid,
    envelope?.payload,
  ]);

  return {
    state,
    dispatch,
    status,
  };
};

import { useState } from 'react';
import { StatefulTransactionAdapter } from '@adapters/transaction/stateful';
import { ZodError } from 'zod';
import { MoneySchema } from '@core/types/finance';

/**
 * ADR-02: Domain Types
 * Deze interfaces horen eigenlijk in src/domain/types.ts
 * maar staan hier voor demonstratie
 */
export interface Transaction {
  id: string;
  amount: number;
  currency: string;
  description: string;
  date: string;
}

/**
 * Custom Hook: useTransactionHistory
 *
 * @architecture
 * - ADR-01 (SoC): Scheiding tussen UI en Business Logic
 * - ADR-03: Adapter patroon voor state management
 * - ADR-04: "Dumb UI" - Hook bevat alle orchestratie
 * - ADR-06: Defensive programming met type guards
 * - ADR-16: Lazy initialization voor performance
 *
 * @responsibilities
 * - Transaction state management
 * - Error handling en validation
 * - Undo/Redo orchestratie via adapter
 * - Type-safe output garantie
 */
export const useTransactionHistory = (initialData: Transaction[] = []) => {
  // ADR-16: Lazy initialization - adapter wordt slechts 1x aangemaakt
  const [adapter] = useState(() => new StatefulTransactionAdapter(initialData));

  // Local state management
  const [transactions, setTransactions] = useState<Transaction[]>(initialData);
  const [error, setError] = useState<string | null>(null);

  /**
   * ADR-06: Defensive Programming
   * Garandeer dat output altijd een werkbare array is
   * Voorkomt runtime crashes bij undefined/null state
   */
  const safeTransactions = Array.isArray(transactions) ? transactions : [];

  /**
   * Update transactie met business rule validation
   * @throws ZodError bij floats (ADR-05: integers only)
   */
  const updateTransaction = (inputValue: number, parts: number) => {
    setError(null);
    try {
      // ADR-05: Valideer input vr business logic
      MoneySchema.parse({ amount: inputValue, currency: 'EUR' });

      // ADR-03: Adapter bevat de domain logic
      const distribution = adapter.calculateDistribution(inputValue, parts);
      const currentView = adapter.getCurrentState();

      const newState = {
        ...currentView,
        distribution,
        lastUpdated: new Date().toISOString(),
      };

      // ADR-12: Audit trail via adapter
      adapter.push(newState, 'USER_UPDATE');

      // In productie zou dit via een subscription/observer pattern gaan
      // Voor nu: direct state sync (optioneel)
      // setTransactions(adapter.getCurrentState());
    } catch (err) {
      // ADR-05: Type-safe error handling
      if (err instanceof ZodError) {
        setError('Input violation: Integers only (ADR-05)');
      } else {
        setError('An unexpected error occurred');
      }
    }
  };

  /**
   * Undo naar vorige state
   * ADR-03: Delegeert naar adapter
   */
  const undo = () => {
    const prevState = adapter.undo();
    if (prevState) {
      // In productie: sync met global state/context
      // Voor nu: no-op (demo purposes)
    }
  };

  /**
   * Redo naar volgende state
   * ADR-03: Delegeert naar adapter
   */
  const redo = () => {
    const nextState = adapter.redo();
    if (nextState) {
      // In productie: sync met global state/context
    }
  };

  /**
   * Clear alle transacties
   * ADR-04: Pure state mutation, geen side effects
   */
  const clearAll = () => {
    setTransactions([]);
  };

  /**
   * Interface Segregation Principle (ISP)
   * Exposeer alleen wat de UI echt nodig heeft
   *
   * @returns Clean API voor consumer components
   */
  return {
    // Data
    transactions: safeTransactions,
    error,

    // Actions
    updateTransaction,
    undo,
    redo,
    clearAll,

    // Debug (ADR-17: Accepted Risk voor development tools)
    _debugAdapterState: adapter.getCurrentState(),
  };
};

import { dataOrchestrator } from '@services/dataOrchestrator';
 // removed unused collectAndDistributeData from '@services/privacyHelpers';
import { Member, CsvItem, } from '@domain/types'; //FinancialIncomeSummary stond er ook bij

export interface ImportResult {
  transactions: CsvItem[];
  summary: {
    source: string;
    finalIncome: number;
    isDiscrepancy: boolean;
  };
  hasMissingCosts: boolean;
  researchPayload: any;
}

export class ImportOrchestrator {
  /**
   * Bridge naar de domein-wasstraat. 
   * Gebruikt destructured import voor collectAndDistributeData conform broncode.
   */
  processCsvImport(params: {
    csvText: string;
    members: Member[];
    setupData: any;
  }): ImportResult {
    const rawResult = dataOrchestrator.processAllData(
      params.members,
      params.csvText,
      params.setupData
    );

    // De wasstraat geeft data terug in de buckets 'local' en 'research'
    const finance = rawResult.local.finance;

    return {
      transactions: finance.transactions,
      summary: {
        source: finance.summary.source,
        finalIncome: finance.summary.finalIncome,
        isDiscrepancy: finance.summary.isDiscrepancy
      },
      hasMissingCosts: finance.hasMissingCosts,
      researchPayload: rawResult.research
    };
  }
}

import { FormState } from '@shared-types/form';
import { Member } from '@domain/household';
import { isSpecialInvestigationRequired } from '@domain/rules/householdRules';

export const aggregateExportData = (state: FormState) => {
  // 1. Data veilig ophalen
  const setup = state.data?.setup || {};
  const household = state.data?.household || { members: [] };
  const finance = state.data?.finance || { income: { items: [] }, expenses: { items: [] } };

  // 2. Variabelen definiren
  const totalAdults = Number(setup.aantalVolwassen || 0);

  // 3. Mapping
  const members = (household.members || []).map((m: Member) => ({
    type: m.memberType,
    leeftijd: m.age || 0,
  }));

  const incomeItems = finance.income?.items || [];
  const expenseItems = finance.expenses?.items || [];

  // 4. Return het object met de Rule uit het domein
  return {
    version: '1.0-phoenix-export',
    schemaVersion: state.schemaVersion || 1,
    //  HIER gebruiken we de officile rule voor n8n
    isSpecialStatus: isSpecialInvestigationRequired(totalAdults), 
    exportDate: new Date().toISOString().split('T')[0],
    household: {
      totalAdults,
      members,
    },
    finances: {
      income: incomeItems,
      expenses: expenseItems,
    },
  };
};

import * as React from 'react';
import { InputCounter } from '@ui/components/fields/InputCounter';
import { useForm } from '@app/context/FormContext';

export const AdultsCounter: React.FC = () => {
  const { state, dispatch } = useForm();
  const currentCount = state.data.setup?.aantalVolwassen ?? 1;

  const handleUpdate = (val: number) => {
    // Handmatige min/max check omdat de component het niet doet
    const sanitizedVal = Math.max(1, Math.min(10, val));

    dispatch({
      type: 'SET_FIELD',
      payload: {
        section: 'setup',
        field: 'aantalVolwassen',
        value: sanitizedVal,
      },
    });
  };

  return (
    <InputCounter
      fieldId="aantalVolwassen"
      value={currentCount}
      stateWriter={handleUpdate as any} valueProvider={undefined}    />
  );
};

export default AdultsCounter;

