import { collectAndDistributeData, assertNoPIILeak } from '@services/privacyHelpers';
import { Logger } from '@/adapters/audit/AuditLoggerAdapter';
/* ============================================================
 * TYPES & INTERFACES
 * ============================================================ */

export interface MasterProcessResult {
  /** Laag A: Voor de UI (Lokaal, bevat PII) */
  local: {
    [DATA_KEYS.HOUSEHOLD]: {
      members: Member[];
    };
    [DATA_KEYS.FINANCE]: {
      transactions: CsvItem[];
      summary: FinancialIncomeSummary;
      hasMissingCosts: boolean;
    };
  };
  /** Laag B: Voor onderzoek (Geanonimiseerd, VEILIG) */
  research: {
    memberPayloads: ResearchPayload[];
    financialAnalytics: {
      totalIncomeCents: number;
      categoryTotals: Record<string, number>;
      timestamp: string;
    };
  };
}

/* ============================================================
 * MASTER ORCHESTRATOR
 * ============================================================ */

export const dataOrchestrator = {
  /**
   * Verwerkt zowel handmatige input (leden) als bank-CSV data.
   * Past de 'Wasstraat-methode' toe: Filteren -> Strippen -> Categoriseren.
   */
  processAllData: (
    rawMembers: RawUIData[],
    rawCsv: string,
    currentSetup: any,
  ): MasterProcessResult => {
    // --- 1. VERWERK LEDEN (Privacy Sluis) ---
    const processedMembers = (rawMembers || []).map((m, i) => collectAndDistributeData(m, i));

    // --- 2. VERWERK CSV (Met Kwaliteitsfilter & PII Strip) ---
    // --- 2. VERWERK CSV (Met Kwaliteitsfilter & PII Strip) ---
    let csvTransactions: CsvItem[] = [];

    try {
      const mapped = (csvService.mapToInternalModel(rawCsv) || []) as CsvItem[];

      csvTransactions = mapped
        .map((item: CsvItem) => ({
          ...item,
          date: item.date || new Date().toISOString(),
          description: dataProcessor.stripPII(item.description || 'Geen omschrijving'),
          category: dataProcessor.categorize(item.description || ''),
        }))
        .filter((tx) => tx.amount !== 0); // Alleen checken op financile waarde
    } catch (e) {
      Logger.error('CSV Mapping failed in Orchestrator', e);
      csvTransactions = [];
    }

    // --- 3. RECONCILIATIE & ANALYSE ---
    // Cast naar FinancialIncomeSummary om TS 'string vs "CSV"|"Setup"' error te fixen
    const incomeSummary = dataProcessor.reconcileWithSetup(
      csvTransactions,
      currentSetup,
    ) as FinancialIncomeSummary;

    // Check op gemiste vaste lasten
    const hasMissingCosts = csvTransactions.some(
      (t) => t.category === 'Wonen' && !currentSetup?.housingIncluded,
    );

    // --- 4. RESEARCH PAYLOAD (Anoniem) ---
    const categoryTotals = csvTransactions.reduce(
      (acc, curr) => {
        const key = curr.category || 'Overig';
        acc[key] = (acc[key] || 0) + (curr.amount || 0);
        return acc;
      },
      {} as Record<string, number>,
    );

    const researchData: MasterProcessResult['research'] = {
      memberPayloads: processedMembers.map((p) => p.researchPayload),
      financialAnalytics: {
        totalIncomeCents: incomeSummary.finalIncome || 0,
        categoryTotals,
        timestamp: new Date().toISOString(),
      },
    };

    // De ultieme check: mag deze data naar de cloud?
    assertNoPIILeak(researchData as unknown as Record<string, unknown>);

    // --- 5. FINALE OUTPUT ---
    return {
      local: {
        [DATA_KEYS.HOUSEHOLD]: {
          members: processedMembers.map((p) => p.localMember),
        },
        [DATA_KEYS.FINANCE]: {
          transactions: csvTransactions,
          summary: incomeSummary,
          hasMissingCosts,
        },
      },
      research: researchData,
    };
  },
};

export default dataOrchestrator;

import { toCents } from '@domain/helpers/numbers';

export type SetupData = {
  maandelijksInkomen?: number; // in euro (wordt naar centen geconverteerd)
  housingIncluded?: boolean;
};

export type CsvItem = {
  amount: number; // in CENTEN (van csvService)
  description: string;
  date: string;
  original: Record<string, unknown>;
  category?: string;
};

export const dataProcessor = {
  // A. Strip IBAN (PII-vrij maken)
  stripPII: (description: string): string => {
    const ibanRegex = /\b[A-Z]{2}\d{2}[A-Z0-9]{4}\d{7,}\b/gi;
    return description.replace(ibanRegex, 'NL** [REDACTED]');
  },

  // B. Categoriseren (uitgebreid + veilig)
  categorize: (description: string): string => {
    const desc = description.toLowerCase();

    if (desc.includes('huur') || desc.includes('hypotheek') || desc.includes('woon'))
      return 'Wonen';

    if (
      desc.includes('zorg') ||
      desc.includes('zilveren') ||
      desc.includes('vgz') ||
      desc.includes('cz')
    )
      return 'Zorgverzekering';

    if (
      desc.includes('supermarkt') ||
      desc.includes('ah ') ||
      desc.includes('jumbo') ||
      desc.includes('lidl')
    )
      return 'Boodschappen';

    if (desc.includes('salaris') || desc.includes('loon') || desc.includes('stipendium'))
      return 'Inkomen';

    return 'Overig';
  },

  // C. Inkomens- & Lasten-vergelijking (type-safe + diff + NaN-proof)
  reconcileWithSetup: (csvData: CsvItem[], setupData: SetupData) => {
    const THRESHOLD_CENTS = 5000; // 50

    const csvIncome = csvData
      .filter((d) => dataProcessor.categorize(d.description) === 'Inkomen')
      .reduce((acc, curr) => acc + (Number.isFinite(curr.amount) ? curr.amount : 0), 0);

    const setupIncomeCents = toCents(setupData.maandelijksInkomen ?? 0);

    const finalIncome = csvIncome > 0 ? csvIncome : setupIncomeCents;
    const source = csvIncome > 0 ? 'CSV' : 'Setup';

    const diff = csvIncome - setupIncomeCents;

    const isDiscrepancy = csvIncome > 0 && Math.abs(diff) > THRESHOLD_CENTS;

    return { finalIncome, source, isDiscrepancy, diff };
  },
};

