      },
    },
    meta: { lastModified: isoNow(), version: 1 },
  };

  const { data: dataOverride, meta: metaOverride, ...topOverrides } = overrides;

  return {
    ...baseState,
    ...topOverrides,
    data: dataOverride ? deepMerge(baseState.data, dataOverride) : baseState.data,
    meta: metaOverride ? { ...baseState.meta, ...metaOverride } : baseState.meta,
  };
}

export function makePhoenixState(overrides?: jest.MockOverrides): FormState {
  const base: FormState = {
    schemaVersion: '1.0',
    activeStep: 'LANDING',
    currentPageId: 'setup',
    isValid: true,
    data: {
      setup: { aantalMensen: 0, aantalVolwassen: 0, autoCount: 'Nee' },
      household: { members: [] },
      finance: { income: { items: [] }, expenses: { items: [] } },
    },
    meta: { lastModified: isoNow(), version: 1 },
  };

  const { data: dataOverride, ...topOverrides } = overrides || {};
  return {
    ...base,
    ...topOverrides,
    data: deepMerge(base.data, dataOverride),
  } as FormState;
}

/**
 * Meet hoe snel een async migratie/transform verloopt.
 * Gebruikt performance.now() indien beschikbaar, anders Date.now().
 */
export async function measureMigrationSpeed<T>(migrationFn: () => Promise<T>) {
  const now =
    typeof performance !== 'undefined' && performance.now
      ? () => performance.now()
      : () => Date.now();

  const start = now();
  const result = await migrationFn();
  const end = now();

  return {
    result,
    duration: `${(end - start).toFixed(2)}ms`,
  };
}

