import * as React from 'react';
import { View, Text, TextInput } from 'react-native';
import { useAppStyles } from '@ui/styles/useAppStyles';
import { toCents, formatCentsToDutch, formatDutchValue } from '@app/orchestrators/types';

type MoneyInputProps = {
  value: number; // Bedrag in centen (altijd >= 0)
  onValueChange: (cents: number) => void;
  disabled?: boolean;
  placeholder?: string;
  accessibilityLabel?: string;
};

const MoneyInput: React.FC<MoneyInputProps> = ({
  value,
  onValueChange,
  disabled = false,
  placeholder = '',
  accessibilityLabel = 'Bedrag',
}) => {
  const { styles } = useAppStyles();

  // De lokale staat houdt de string vast die de gebruiker ziet
  const [localValue, setLocalValue] = React.useState<string>(formatCentsToDutch(value));

  // Sync lokale staat als de externe waarde (centen) wijzigt
  React.useEffect(() => {
    setLocalValue(formatCentsToDutch(value));
  }, [value]);

  // Bij focus: verwijder duizendtal-punten voor makkelijk bewerken
  const onFocus = () => setLocalValue(formatDutchValue(localValue));

  // Tijdens typen: saniteer direct (geen letters, geen minus)
  const onChangeText = (text: string) => setLocalValue(formatDutchValue(text));

  // Bij blur: bereken centen en formatteer naar NL-standaard
  const onBlur = () => {
    const cents = toCents(localValue);
    onValueChange(cents); // Emit positieve centen naar de state
    setLocalValue(formatCentsToDutch(cents)); // "On-blur formatting"
  };

  return (
    <View style={styles.inputMoneyRow}>
      {/* Vast, niet-bewerkbaar prefix  */}
      <Text style={styles.moneyPrefix} accessibilityLabel="Euro-teken">

      </Text>

      <TextInput
        value={localValue}
        onChangeText={onChangeText}
        onFocus={onFocus}
        onBlur={onBlur}
        placeholder={placeholder}
        editable={!disabled}
        accessibilityLabel={accessibilityLabel}
        keyboardType="decimal-pad" // Forceert numeriek pad op mobile
        style={styles.moneyTextInput}
      />
    </View>
  );
};

export default MoneyInput;

/**
 * Format date object of ISO string naar verschillende weergaven:
 * 'dd-mm-yyyy' | 'weekday' | 'short' | 'full'
 *
 * - 'YYYY-MM-DD' wordt TZ-robuust behandeld via lokale noon.
 * - Strings met tijd laten we bewust aan new Date(...) (heeft al offset/context).
 * - Een Date blijft een Date.
 */
export function formatDate(
    input: Date | string,
    formatType: 'dd-mm-yyyy' | 'weekday' | 'short' | 'full' = 'dd-mm-yyyy',
  ): string {
    let date: Date;
    if (typeof input === 'string' && /^\d{4}-\d{2}-\d{2}$/.test(input)) {
      // Parseer ISO-dagstring TZ-robuust
      const [yy, mm, dd] = input.split('-').map(Number);
      const test = new Date(yy, mm - 1, dd, 12, 0, 0, 0);
      if (test.getFullYear() !== yy || test.getMonth() !== mm - 1 || test.getDate() !== dd) return '';
      date = test;
    } else if (typeof input === 'string') {
      const d = new Date(input);
      if (isNaN(d.getTime())) return '';
      date = d;
    } else {
      date = input;
    }
    if (!date || isNaN(date.getTime())) {
      return '';
    }
    const options: Intl.DateTimeFormatOptions = {};
    switch (formatType) {
      case 'dd-mm-yyyy': {
        const dd = String(date.getDate()).padStart(2, '0');
        const mm = String(date.getMonth() + 1).padStart(2, '0');
        const yyyy = date.getFullYear();
        return `${dd}-${mm}-${yyyy}`;
      }
      case 'weekday': {
        options.weekday = 'short';
        return date.toLocaleDateString('nl-NL', options);
      }
      case 'short': {
        options.day = 'numeric';
        options.month = 'short';
        options.year = '2-digit';
        return date.toLocaleDateString('nl-NL', options);
      }
      case 'full': {
        options.weekday = 'long';
        options.day = 'numeric';
        options.month = 'long';
        options.year = 'numeric';
        return date.toLocaleDateString('nl-NL', options);
      }
      default:
        return date.toLocaleDateString('nl-NL');
    }
  }

  /** Formateert Date of ISO naar YYYY-MM-DD. */
export function formatDateISO(input: Date | string): string {
    let date: Date;
    if (typeof input === 'string' && /^\d{4}-\d{2}-\d{2}$/.test(input)) {
      const [yy, mm, dd] = input.split('-').map(Number);
      const test = new Date(yy, mm - 1, dd, 12, 0, 0, 0);
      if (test.getFullYear() !== yy || test.getMonth() !== mm - 1 || test.getDate() !== dd) return '';
      date = test;
    } else if (typeof input === 'string') {
      const d = new Date(input);
      if (isNaN(d.getTime())) return '';
      date = d;
    } else {
      date = input;
    }
    if (!date || isNaN(date.getTime())) return '';
    const yyyy = date.getFullYear();
    const mm = String(date.getMonth() + 1).padStart(2, '0');
    const dd = String(date.getDate()).padStart(2, '0');
    return `${yyyy}-${mm}-${dd}`;
  }

// src/ui/screens/CSV/CsvUploadScreen.tsx
import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, ActivityIndicator, Alert } from 'react-native';
import { UI_SECTIONS } from '@ui/constants/uiSections';
import { placeholderStyles } from '@/ui/styles/modules/placeholderStyles';

//  Type for FormStateOrchestrator
interface ImportResult {
  transactions: Array<{
    id: string;
    amount: number;
    description: string;
  }>;
}

interface FormStateOrchestrator {
  importCsvData: (csvText: string) => Promise<ImportResult>;
}

interface CsvUploadScreenProps {
  formStateOrchestrator: FormStateOrchestrator;
}

export const CsvUploadScreen: React.FC<CsvUploadScreenProps> = ({
  formStateOrchestrator
}) => {
  const [csvText, setCsvText] = useState('');
  const [isUploading, setIsUploading] = useState(false);

  const handleUpload = async () => {
    try {
      setIsUploading(true);
      const result = await formStateOrchestrator.importCsvData(csvText);

      if (!result || result.transactions?.length === 0) {
        Alert.alert('Fout', 'Geen geldige transacties gevonden.');
        return;
      }

      Alert.alert(
        'Bevestig Upload',
        `Transacties verwerkt: ${result.transactions.length}`
      );
    } catch (e) {
      //  FIX: Proper error handling
      const errorMessage = e instanceof Error ? e.message : 'Onbekende fout';
      Alert.alert('Fout', 'Fout bij verwerken: ' + errorMessage);
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <View style={placeholderStyles.screenContainer}>
      <Text style={placeholderStyles.screenTitle}>{UI_SECTIONS.CSV_UPLOAD}</Text>

      <TextInput
        style={placeholderStyles.textArea}
        value={csvText}
        onChangeText={setCsvText}
        placeholder="Plak CSV-gegevens hier..."
        editable={!isUploading}
        multiline
        numberOfLines={10}
        textAlignVertical="top"
      />

      <TouchableOpacity
        style={[
          placeholderStyles.primaryButton,
          (isUploading || !csvText.trim()) && placeholderStyles.buttonDisabled
        ]}
        onPress={handleUpload}
        disabled={isUploading || !csvText.trim()}
        accessibilityRole="button"
        accessibilityLabel={isUploading ? 'Bezig met verwerken' : 'Verwerk CSV'}
      >
        {isUploading ? (
          <ActivityIndicator color="#fff" />
        ) : (
          <Text style={placeholderStyles.fieldCountText}>Verwerk CSV</Text>
        )}
      </TouchableOpacity>
    </View>
  );
};

export default CsvUploadScreen;

