// src/styles/useAppStyles.ts
// CU-008.6: De Orchestrator (Main Factory)
import { StyleSheet } from 'react-native';
import { useTheme } from '@app/context/ThemeContext';
import { Colors, Theme } from '@styles/Colors';
import { Tokens } from '@styles/Tokens';
import {
  makeLayout,
  makeHeader,
  makeForms,
  makeButtons,
  makeCards,
  makeChips,
  makeDashboard,
  makeSummary,
  makeTypography,
  makeAlerts,
  makeToggles, //  NIEUW
  makeCheckboxes, //  NIEUW
  makeHelpers, //  NIEUW
} from '@styles/modules';

const styleCache: Partial<Record<Theme, ReturnType<typeof StyleSheet.create>>> = {};

export function getAppStyles(theme: Theme) {
  if (styleCache[theme]) return styleCache[theme]!;
  const c = Colors[theme];

  const assembled = {
    ...makeLayout(c), // Layout
    ...makeHeader(c), // Header
    ...makeForms(c), // Forms
    ...makeButtons(c), // Buttons
    ...makeCards(c), // Cards
    ...makeChips(c), // Chips
    ...makeDashboard(c), // Dashboard
    ...makeSummary(c), // Summary
    ...makeTypography(c), // Typography
    ...makeAlerts(c), // Alerts
    ...makeToggles(c), // Toggles NIEUW
    ...makeCheckboxes(c), //  Checkboxes NIEUW
    ...makeHelpers(c), //  Helpers NIEUW
  } as const;

  const styles = StyleSheet.create(assembled);
  styleCache[theme] = styles;
  return styles;
}

export type AppStyles = ReturnType<typeof getAppStyles>;

export function useAppStyles() {
  const { theme } = useTheme();
  const styles = getAppStyles(theme);
  const colors = Colors[theme];
  return { styles, colors, Tokens };
}

/** 
 * csvHelper.ts
 * Universele CSV naar JSON parser.
 * Ondersteunt: Komma (,), Puntkomma (;) en Tabs (\t).
 */
export const parseRawCsv = (text: string): Record<string, string>[] => {
  if (!text) return [];

  // 1. Schoon de tekst op (verwijder Byte Order Marks)
  const cleanText = text.replace(/^\uFEFF/, '').trim();
  const lines = cleanText.split(/\r?\n/); // Werkt voor zowel Windows als Unix line-endings

  if (lines.length < 2) return [];

  // 2. Detecteer de scheider op basis van de eerste regel
  let delimiter = ',';
  if (lines[0].includes('\t')) delimiter = '\t';
  else if (lines[0].includes(';')) delimiter = ';';

  // 3. Extraheer headers
  const headers = lines[0].split(delimiter).map((h) => h.trim().replace(/"/g, ''));

  // 4. Map de rijen naar objecten
  return lines.slice(1).map((line) => {
    const values = line.split(delimiter);
    return headers.reduce(
      (obj, header, i) => {
        // Verwijder eventuele aanhalingstekens rondom waarden
        const val = values[i] ? values[i].trim().replace(/"/g, '') : '';
        obj[header] = val;
        return obj;
      },
      {} as Record<string, string>,
    );
  });
};
